USE [master];

CREATE TABLE MICROSERVICE.DESKS(DESK_ID UNIQUEIDENTIFIER NOT NULL DEFAULT (NEWID()), DESK_NUMBER VARCHAR(256) NOT NULL, AVAILABLE BIT DEFAULT 1, VERSION BIGINT DEFAULT 0, CONSTRAINT DESKS_PK PRIMARY KEY(DESK_ID))

CREATE TABLE MICROSERVICE.FLOOR_DESKS(FLOOR_DESK_ID UNIQUEIDENTIFIER NOT NULL DEFAULT (NEWID()), FLOOR_ID UNIQUEIDENTIFIER, DESK_ID UNIQUEIDENTIFIER, VERSION BIGINT DEFAULT 0, CONSTRAINT FLOOR_DESK_PK PRIMARY KEY(FLOOR_DESK_ID), CONSTRAINT FLOOR_DESK_UQ UNIQUE(FLOOR_ID, DESK_ID), CONSTRAINT FLOOR_DESKS_FLOOR_FK FOREIGN KEY(FLOOR_ID) REFERENCES MICROSERVICE.FLOORS(FLOOR_ID), CONSTRAINT FLOOR_DESKS_DESK_FK FOREIGN KEY(DESK_ID) REFERENCES MICROSERVICE.DESKS(DESK_ID));
